import { useState } from 'react';
import './ExpensesForm.css'
function ExpensesForm()
{
    const[enteredTitle,setEnteredTitle] = useState('');
    const[enteredDate,setEnteredDate] = useState('');
    const[enteredCost,setEnteredCost] = useState('');
    function titleChangeHandler(event)
    {
        setEnteredTitle(event.target.value);
    }
    function dateChangeHandler(event)
    {
        setEnteredDate(event.target.value);
    }
    function costChangeHandler(event)
    {
        setEnteredCost(event.target.value);
    }
    function formSubmitHanlder(event)
    {
        event.preventDefault();
        const expenses ={
            title:enteredTitle,
            date:enteredDate,
            cost:enteredCost
        }
        Console.log(expenses);
    }
    return(
        <form onSubmit={formSubmitHanlder}>
            <div className="new-expense_controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange ={titleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" onChange={dateChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Cost</label>
                    <input type="number" min="0.01" step="1" onChange={costChangeHandler}></input>
                </div>
                <div className="new-expense__actions">
                <button>Submit</button>
                </div>
            </div>
        </form>
    )
}
export default ExpensesForm;
