import { useState } from 'react';
import Expenses from './Components/Expenses'
import NewExpenses from './NewExpenses/NewExpenses'

// const dummy_Data = [{id:'1',title: "book", cost:100, date: new Date(2022,2,25)},
//   {id:'2',title: "pen", cost:10, date: new Date(2022,2,26)},
//   {id:'3',title: "bench", cost:1000, date: new Date(2022,2,27)}];

function App() {
    const [expenses, setExpenses] = useState([{id:'1',title: "book", cost:100, date: new Date(2022,2,25)},
    {id:'2',title: "pen", cost:10, date: new Date(2022,2,26)},
    {id:'3',title: "bench", cost:1000, date: new Date(2022,2,27)}]);
    const BringExpenseData = (expense) =>{
      //  console.log(expense);
      debugger;
       var expensesTemp = expenses;
      //  expensesTemp.push(expense);
      //  console.log(expensesTemp);
       setExpenses([expense, ...expensesTemp]);
      // setExpenses(expene);
    };
  return (
    <div>
      <NewExpenses onBringExpenseData={BringExpenseData}></NewExpenses>
      {expenses.length>0 ? <Expenses d = {expenses}></Expenses>:""};

    </div>
  );
}

export default App;
