import { useState } from 'react';
import Expenses from './Components/Expenses'
import NewExpenses from './NewExpenses/NewExpenses'

const data = [{id:'1',title: "book", cost:100, date: new Date(2022,2,25)},
  {id:'2',title: "pen", cost:10, date: new Date(2022,2,26)},
  {id:'3',title: "bench", cost:1000, date: new Date(2022,2,27)}];

function App() {
  const [exp,setExp]= useState(data);

  function BringExpenseData(expenses)
  {
    setExp((prevExp)=>{
      return [expenses,...prevExp];
    })
    // console.log(expenses);
  }
  return (
    <div>
      <NewExpenses onBringExpenseData={BringExpenseData}></NewExpenses>
      <Expenses d = {exp}></Expenses>
    </div>
  );
}

export default App;
